import React from 'react';
import { Navbar, Nav } from "react-bootstrap";
import { BrowserRouter as Router, Route, Switch, Link } from 'react-router-dom';

import Home from './pages/site/home.site';
import About from './pages/site/about.site';
import Ministry from './pages/site/ministry.site';

import Admin from './pages/admin/admin';
import AdminLogin from './pages/admin/login.admin';

import Logo from "./assets/img/lighthouse_logo_1.png"

function App() {
  return (
    <Router>
      <Navbar bg='dark' variant='dark'>
        <Navbar.Brand href="/"><img src={Logo} width='30' height='30' alt="lighthouse logo"/></Navbar.Brand>
        <Nav>
          <Nav.Link href="/">Home</Nav.Link>
          <Nav.Link href="/about">About</Nav.Link>
          <Nav.Link href="/ministry">Ministry</Nav.Link>
        </Nav>
      </Navbar>

        <Switch>
          <Route exact path="/" component={Home}/>
          <Route path="/ministry" component={Ministry} />
          <Route path="/about" component={About} />

          <Route path="/admin/login" component={AdminLogin} />
          <Route path="/admin" component={Admin} />
        </Switch>
    </Router>
  );
}

export default App;
